var consolidate = require('consolidate-build');
var node = require('then-node');

function deepPromisify(obj, into) {
  Object.keys(obj)
    .forEach(function (key) {
      if (typeof obj[key] === 'function') {
        into[key] = node(obj[key]);
        deepPromisify(obj[key], into[key]);
      } else {
        into[key] = obj[key];
      }
    });
}
deepPromisify(consolidate, exports);